sections.code:
/* Remove Logo */
[aria-label="Twitter"] {
  display: none;
}




/*
* LEFT COLUMN
*/

/* Remove nav I don't use */
[aria-label="Search and explore"],
[aria-label="Bookmarks"],
[aria-label="Lists"] {
  display: none;
}

/* Less visual weight for nav options */
[aria-label="Primary"] [dir="auto"] {
  font-size: 1rem;
  font-weight: 400;
}

/* Tigher grouping */
[aria-label="Primary"] a {
  padding: 0;
}

/* Hide distracting profile toggle */
[data-testid="SideNav_AccountSwitcher_Button"] {
  display: none;
}


/*
* TWEET BUTTON
*/

/* Make button flatter, less loud */
[aria-label="Tweet"] {
  border: none;
  box-shadow: none;
  background-color: #F4F8FA;
}

/* Style to look like other UI */
[aria-label="Tweet"] div {
  color: #00A1F9;
}

[aria-label="Tweet"]:hover {
  filter: none;
}

[aria-label="Tweet"]:hover {
  background-color: #00A1F9;
}

[aria-label="Tweet"]:hover div {
  color: #F4F8FA;
}




/*
* RIGHT COLUMN
*/

/* See ya later, racism aggregator */
[aria-label="Timeline: Trending now"] {
  display: none;
}

/* Less visual weight for who to follow */
[aria-label="Who to follow"] [aria-level="2"][role="heading"] .r-1qd0xha {
  font-size: 1rem;
}

/* Tone down unfollow */
[data-testid="16515870-unfollow"] {
  filter: grayscale(1);
  opacity: 0.5;
}

/* Remove the background color from Who to Follow */
[data-testid="sidebarColumn"] .r-1u4rsef {
  background-color: transparent !important;
}


/*
* DM DRAWER
*/

[data-testid="DMDrawer"] {
  display: none;
}

.LeftnavList {
  width: 220px;
}

.LeftnavListRow__text {
  width: 100px;
}

.feedlyChrome--leftnav-open .LeftnavPin {
  left: 284px;
}

.feedlyChrome--leftnav-open #feedlyFrame {
  margin-left: 294px;
}

.feedlyChrome--leftnav-open .fixed-bar {
  left: 324px;
}

.fx button.primary.pro {
  display: none;
}

.tertiary.button-icon-only[title="Create Team Newsletter"],
.tertiary.button-icon-only[title="Share with teammates"]{
  display: none;
}

html.simpl:not(.splitView) .AO .aeF > .nH  {
  max-width: 30000px !important;
}

.l7 {
  background-color: transparent;
  font-weight: normal;
}
